
public class forLoopPattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1+2+3 sum malvu jove
		int num=10;
		int sum=0;
		for(int i=1; i<=num ;i++) //num times loop farse
			{
			sum=sum+i;
			
		}
		System.out.println("the sum is: " + sum);

	}

}
